import{_ as t,g as a}from"./index-3UE7mZEC.js";const s={},d=a('<div class="banner" data-v-01781f76></div><div class="wrap" data-v-01781f76><p class="txt" data-v-01781f76><span class="title" data-v-01781f76>编者按：</span> 2023年11月29日，是习近平总书记给人民教育出版社老同志回信三周年的日子。三年来，在习近平总书记重要回信精神的指引下，人教社始终牢记总书记殷殷嘱托，牢牢把握正确的政治方向和出版导向，把“培根铸魂、启智增慧”的要求落实在人教社高质量改革发展的具体行动中。未来，人教社将继续学习贯彻落实习近平总书记重要回信精神、学习贯彻落实党的二十大精神，坚定不移打造精品教材和精品出版物，为培养德智体美劳全面发展的社会主义建设者和接班人接续奋斗。 </p><div class="card" data-v-01781f76><h2 style="text-align:center;" data-v-01781f76>习近平给人民教育出版社老同志回信强调 <br data-v-01781f76> 紧紧围绕立德树人根本任务 用心打造培根铸魂启智增慧的精品教材</h2><p class="txt" style="font-size:20px;" data-v-01781f76> 新华社北京2020年11月30日电 中共中央总书记、国家主席、中央军委主席习近平近日给人民教育出版社部分离退休老同志回信，向人教社的全体同志致以问候，对教材编研出版工作提出殷切期望。 <br data-v-01781f76> 　　习近平在回信中说，70年来，人民教育出版社在基础教育教材和教育图书编研出版上辛勤耕耘、接续奋斗，为我国教育事业发展作出了积极贡献，我谨向你们和全社员工致以诚挚的问候！ <br data-v-01781f76> 　　习近平强调，百年大计，教育为本。希望人民教育出版社紧紧围绕立德树人根本任务，坚持正确政治方向，弘扬优良传统，推进改革创新，用心打造培根铸魂、启智增慧的精品教材，为培养德智体美劳全面发展的社会主义建设者和接班人、建设教育强国作出新的更大贡献。 <br data-v-01781f76> 　　人民教育出版社成立于1950年，始终致力于基础教育教材和教育图书的研究、编写、出版和发行，近年来还承担了中小学道德与法治（思想政治）、语文和历史三科统编教材的编辑出版任务。70年来，人教社先后编研出版了11套全国通用中小学教材，累计出版各类出版物7万余种，发行量逾750亿册。近日，该社部分离退休老同志给习近平总书记写信，代表全社同志汇报了人教社70年来履行编书育人使命的情况，表达了为繁荣我国教育出版事业贡献力量的决心。 </p><div style="text-align:center;" data-v-01781f76><img src="https://www.pep.com.cn/xw/zt/ss/hx/202012/W020230106688291540700.jpg" alt="" data-v-01781f76></div></div><div style="height:50px;" data-v-01781f76></div></div>',2);function e(c,v){return d}const i=t(s,[["render",e],["__scopeId","data-v-01781f76"]]);export{i as default};
